import React, { useState, useEffect } from 'react';
import { Button, Form, Select, Input, InputNumber, Modal } from 'antd';


export default function Edit(props) {
    const { isOpen, item, onOk, onCancel } = props;
    const [form] = Form.useForm();

    useEffect(() => {
        if (isOpen) {
            // ถ้า Modal เปิดให้กำหนดค่าใน form จาก props.item
            form.setFieldsValue(item);
        }
    }, [isOpen, item, form]);

    const handleOk = () => {
        // ทำอะไรกับข้อมูลที่ถูกแก้ไข แล้วเรียก onOk
        form
            .validateFields()
            .then(values => {
                onOk(values);
                form.resetFields();
            })
            .catch(error => {
                console.error('Validation failed:', error);
            });
    };

    const handleCancel = () => {
        // ยกเลิกการแก้ไข แล้วเรียก onCancel
        form.resetFields();
        onCancel();
    };


    return (
        <>
        <Button onClick={isOpen} >Edit</Button>
            <Modal title="Basic Modal" open={isOpen} onOk={handleOk} onCancel={handleCancel}>
                <Form form={form} onFinish={handleOk}>
                    <Form.Item
                        name="type"
                        label="ชนิด"
                        rules={[{ required: true }]}
                    >
                        <Select
                            allowClear
                            style={{ width: "100px" }}
                            options={[
                                {
                                    value: 'income',
                                    label: 'รายรับ',
                                },
                                {
                                    value: 'expense',
                                    label: 'รายจ่าย',
                                },
                            ]}
                        />
                    </Form.Item>
                    <Form.Item
                        name="amount"
                        label="จำนวนเงิน"
                        rules={[{ required: true }]}>
                        <InputNumber placeholder="จำนวนเงิน" />
                    </Form.Item>
                    <Form.Item
                        name="note"
                        label="หมายเหตุ"
                        rules={[{ required: true }]}>
                        <Input placeholder="Note" />
                    </Form.Item>
                </Form>
            </Modal>
        </>
    );
};
