import React, { useState } from 'react';
import { Button, Form, Select, Input, InputNumber, Modal} from 'antd';

export default function Edit(props) {
    const [form] = Form.useForm()
    const [isModalOpen, setIsModalOpen] = useState(false);

    const showModal = () => {
        setIsModalOpen(true);
    };

    const handleOk = () => {
        setIsModalOpen(false);
    };

    const handleCancel = () => {
        setIsModalOpen(false);
    };

    return (
        <Modal title="Basic Modal" open={isModalOpen} onOk={handleOk} onCancel={handleCancel}>

        <Form form={form} layout="inline" onFinish={(data) => {
            props.onItemAdded(data);
            form.resetFields();
        }}>
            <Form.Item
                name="type"
                label="ชนิด"
                rules={[{ required: true }]}
            >
                <Select
                    allowClear
                    style={{ width: "100px" }}
                    options={[
                        {
                            value: 'income',
                            label: 'รายรับ',
                        },
                        {
                            value: 'expense',
                            label: 'รายจ่าย',
                        },
                    ]}
                />
            </Form.Item>
            <Form.Item
                name="amount"
                label="จำนวนเงิน"
                rules={[{ required: true }]}>
                <InputNumber placeholder="จำนวนเงิน" />
            </Form.Item>
            <Form.Item
                name="note"
                label="หมายเหตุ"
                rules={[{ required: true }]}>
                <Input placeholder="Note" />
            </Form.Item>
            <Form.Item>
                <Button type="primary" htmlType="submit">Add</Button>
            </Form.Item>
        </Form>
        </Modal>
    )
    return (
        <>
            <Button type="primary" onClick={showModal}>
                Open Modal
            </Button>
            <Modal title="Basic Modal" open={isModalOpen} onOk={handleOk} onCancel={handleCancel}>
                <p>Some contents...</p>
                <p>Some contents...</p>
                <p>Some contents...</p>
            </Modal>
        </>
    );
};
