import { useState, useEffect } from 'react';
import './App.css' ;
import { Spin } from 'antd';
import axios from 'axios'
import TransactionList from "./components/TransactionList" ;

axios.defaults.baseURL = process.env.REACT_APP_BASE_URL || "http://localhost:1337"
const URL_TXACTIONS = '/api/txactions'
function App() {

  const [isLoading, setIsLoading] = useState(false)
  const [transactionData, setTransactionData] = useState([
    {id:1 , action_datetime: "2023-11-18T17:00:00.000Z", amount:1244, note:"sad naha", type : "income"}
  ])

  const fetchItems = async () => {
    try {
      setIsLoading(true)
      const response = await axios.get(URL_TXACTIONS)
      // setTransactionData(response.data.data.map(data => {
      //   return {
      //     id : data.id ,
      //     action_datetime : data.attributes.action_datetime ,
      //     amount : data.attributes.amount ,
      //     note : data.attributes.not ,
      //     type : data.attributes.type 
      //   }
      // }))

      setTransactionData(response.data.data.map(data => ({
        id : data.id ,
        ...data.attributes
      })))

    } catch (err) {
      console.log(err)
    } finally { setIsLoading(false) }
  }

  useEffect(() => {
    fetchItems()
  }, [])

  return (
    <div className="App">
      <header className="App-header">
        <Spin spinning={isLoading}>
          <TransactionList data={transactionData} />
        </Spin>
      </header>
    </div>
  );
}

export default App;
