import { useState, useEffect } from 'react';
import './App.css';
import { Spin, Divider, Typography } from 'antd';
import axios from 'axios'
import TransactionList from "./components/TransactionList";
import AddItem from "./components/AddItem";
import moment from 'moment';

axios.defaults.baseURL = process.env.REACT_APP_BASE_URL || "http://localhost:1337"
const URL_TXACTIONS = '/api/txactions'

function App() {
  const [currentAmount, setCurrentAmount] = useState(0)

  const [isLoading, setIsLoading] = useState(false)
  const [transactionData, setTransactionData] = useState([])

  const fetchItems = async () => {
    try {
      setIsLoading(true)
      const response = await axios.get(URL_TXACTIONS)

      setTransactionData(response.data.data.map(data => ({
        id: data.id,
        key: data.id,
        ...data.attributes
      })))

    } catch (err) {
      console.log(err)
    } finally { setIsLoading(false) }
  }

  const addItem = async (item) => {
    try {
      setIsLoading(true)
      console.log(item);
      const params = { ...item, action_datetime: moment() }
      const response = await axios.post(URL_TXACTIONS, { data: params })
      const { id, attributes } = response.data.data

      setTransactionData([
        ...transactionData,
        {
          id: id,
          key: id,
          ...attributes
        }
      ])

    } catch (err) {
      console.log(err)
    } finally {
      setIsLoading(false)
    }
  }

  const deleteItem = async (itemId) => {
    console.log(itemId)
    try {
      setIsLoading(true)
      await axios.delete(`${URL_TXACTIONS}/${itemId}`)
      fetchItems()
    } catch (err) {
      console.log(err)
    } finally {
      setIsLoading(false)
    }
  }

  useEffect(() => {
    fetchItems()
  }, [])

  useEffect(() => {
    setCurrentAmount(transactionData.reduce(
      (sum, data) => sum = data.type === "income" ? sum + data.amount : sum - data.amount, 0))
  }, [transactionData])

  return (
    <div className="App">
      <header className="App-header">
        <Spin spinning={isLoading}>
          <Typography.Title>
            จํานวนเงินปัจจุบัน {currentAmount} บาท
          </Typography.Title>
          <AddItem onItemAdded={addItem} />
          <Divider>บันทึกรายรับ-รายจ่าย</Divider>
          <TransactionList data={transactionData}
            onTransactionDeleted={deleteItem} />
        </Spin>
      </header>
    </div>
  );
}

export default App;
