import { Space, Table, Tag, Button } from 'antd';
import moment from 'moment';
import { useState } from 'react';
import Edit from './EditItem';

export default function TransactionList(props) {
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [selectedRecord, setSelectedRecord] = useState(null);

  const handleEditClick = (record) => {
    setSelectedRecord(record);
    setIsEditModalOpen(true);
  };

  const handleEditModalOk = (editedData) => {
    // ทำอะไรกับข้อมูลที่ถูกแก้ไข แล้วปิด Modal
    setIsEditModalOpen(false);
    // ส่งข้อมูลที่ถูกแก้ไขไปยัง parent component หรือที่ต้องการ
    props.onTransactionEdited(editedData);
  };

  const handleEditModalCancel = () => {
    // ยกเลิกการแก้ไข แล้วปิด Modal
    setIsEditModalOpen(false);
  };

  const columns = [
    {
      key: 'id',
      title: 'ID',
      dataIndex: "id"
    },
    {
      key: 'action_datetime',
      title: 'Date-Time',
      dataIndex: 'action_datetime',
    },
    {
      key: 'type',
      title: 'Type',
      dataIndex: 'type',
      render: type => type === 'income' ? <Tag color='green'>รายรับ</Tag> : <Tag color='red'>รายจ่าย</Tag>
    },
    {
      key: 'amount',
      title: 'Amount',
      dataIndex: 'amount',
    },
    {
      key: 'note',
      title: 'Note',
      dataIndex: 'note',
    },
    {
      title: 'Action',
      key: 'action',
      render: (_, record) => (
        <Space size="middle">
          <Button onClick={() => props.onTransactionDeleted(record.id)}>Delete</Button>
          <Edit isOpen={isEditModalOpen}
            item={selectedRecord}
            onOk={handleEditModalOk}
            onCancel={handleEditModalCancel} ></Edit>
        </Space>
      ),

    },
  ];


  return (
    <Table columns={columns} dataSource={props.data} />
  )
}
