import React, { useState } from 'react';
import { Button, Form, Select, Input, InputNumber, Modal } from 'antd';

export default function Edit(props) {
    const [form] = Form.useForm()

    return (
        <Form form={form} layout="inline" onFinish={(data) => {
            props.onItemAdded(data);
            form.resetFields();
        }}>
            <Form.Item
                name="type"
                label="ชนิด"
                rules={[{ required: true }]}
            >
                <Select
                    allowClear
                    style={{ width: "100px" }}
                    options={[
                        {
                            value: 'income',
                            label: 'รายรับ',
                        },
                        {
                            value: 'expense',
                            label: 'รายจ่าย',
                        },
                    ]}
                />
            </Form.Item>
            <Form.Item
                name="amount"
                label="จำนวนเงิน"
                rules={[{ required: true }]}>
                <InputNumber placeholder="จำนวนเงิน" />
            </Form.Item>
            <Form.Item
                name="note"
                label="หมายเหตุ"
                rules={[{ required: true }]}>
                <Input placeholder="Note" />
            </Form.Item>
            <Form.Item>
                <Button type="primary" htmlType="submit">Add</Button>
            </Form.Item>
        </Form>
    )

}
