import { useEffect, useState } from 'react';
import { Button, Modal, Form, Select, Input, InputNumber } from 'antd';

export default function Edit(props) {
    const [isModalOpen, setIsModalOpen] = useState(false);

    const [form] = Form.useForm()

    const showModal = () => {
        setIsModalOpen(true);
    };

    const handleOk = () => {
        setIsModalOpen(false);
        props.onOk(form);
    };

    const handleCancel = () => {
        setIsModalOpen(false);
    };

    const handleFormChange = (changedValues, allValues) => {
        setFormData(allValues);
    };

    return (
        <>
            <Button type="primary" onClick={showModal}>
                Edit
            </Button>
            <Modal title="Edit Transaction" open={isModalOpen} onOk={handleOk} onCancel={handleCancel}>
                <Form onFinish={(data) => {
                    props.onItemAdded(data)}} form = { form } >
                    <Form.Item
                        name="type"
                        label="ชนิด"
                        rules={[{ required: true }]}
                    >
                        <Select
                            allowClear
                            style={{ width: "100px" }}
                            options={[
                                {
                                    value: 'income',
                                    label: 'รายรับ',
                                },
                                {
                                    value: 'expense',
                                    label: 'รายจ่าย',
                                },
                            ]}
                        />
                    </Form.Item>
                    <Form.Item
                        name="amount"
                        label="จำนวนเงิน"
                        rules={[{ required: true }]}>
                        <InputNumber placeholder="จำนวนเงิน" />
                    </Form.Item>
                    <Form.Item
                        name="note"
                        label="หมายเหตุ"
                        rules={[{ required: true }]}>
                        <Input placeholder="Note" />
                    </Form.Item>
                </Form>
        </Modal >
        </>
    );
};
