import { Space, Table, Tag, Button } from 'antd';
import moment from 'moment';
import Edit from './EditItem';
import { useState } from 'react';

export default function TransactionList(props) {
  const onItemChanged = (formData) => {
    // ทำอะไรกับข้อมูลที่ได้รับจาก Edit component ได้ที่นี่
    console.log('Received data from Edit:', formData);
  };

  const columns = [
    {
      key: 'id',
      title: 'ID',
      dataIndex: "id"
    },
    {
      key: 'action_datetime',
      title: 'Date-Time',
      dataIndex: 'action_datetime',
    },
    {
      key: 'type',
      title: 'Type',
      dataIndex: 'type',
      render: type => type === 'income' ? <Tag color='green'>รายรับ</Tag> : <Tag color='red'>รายจ่าย</Tag>
    },
    {
      key: 'amount',
      title: 'Amount',
      dataIndex: 'amount',
    },
    {
      key: 'note',
      title: 'Note',
      dataIndex: 'note',
    },
    {
      title: 'Action',
      key: 'action',
      render: (_, record) => (
        <Space size="middle">
          <Button onClick={() => props.onTransactionDeleted(record.id)}>Delete</Button>
          {/* <Button onClick={() => props.onTransactionEdited(record)}>Edit</Button> */}
          <Edit onOk={handleEditOk} onClick={() => props.onTransactionEdited(record)}></Edit>
        </Space>
      ),

    },
  ];


  return (
    <Table columns={columns} dataSource={props.data} />
  )
}
